void ctr(unsigned char* block, unsigned int* num)
{
	(*num)++;
	*(unsigned int*)(block + 4) = *num; // 4바이트 값을 1바이트 값 4개로 나눠주는것
}

void Salsa_then_Mac(unsigned char* buf, int bufsize, unsigned char* key, unsigned char* n)
{
	ECRYPT_ctx x, x2;
	int i;
	unsigned char keystream[64] = { 0, }, keystream2[64] = { 0, };
	unsigned char k1[32] = { 0x23, 0xcd, 0x52, 0x59, 0x57, 0x44, 0xaa, 0xba, 0xd5, 0xc6, 0x52, 0x7d, 0xf2, 0x98, 0xd9, 0x25, 0x35, 0x5a, 0xf7, 0xc6, 0x99, 0x46, 0xe4, 0xfd, 0x8a, 0xd2, 0xfc, 0xc7, 0xa1, 0x9e, 0x53, 0xa7 }, k2[16] = { 0xf, 0x6e, 0x90, 0xa9, 0xe7, 0x22, 0x7f, 0x69, 0xe9, 0x6d, 0x99, 0x2f, 0xad, 0x86, 0x65, 0xb1 }, k3[16] = { 0xfe, 0x50, 0x95, 0x26, 0x73, 0x55, 0x9f, 0x21, 0x1, 0xe1, 0xd1, 0xec, 0x5c, 0x3b, 0x9, 0xb7 };
	unsigned char A[16] = { 0, };
	//unsigned int tempsize = ;
	unsigned char* temp = (unsigned char*)malloc(bufsize + sizeof(A) + 8);
	unsigned char mac[16] = { 0, };

	ghash(n, A, buf, k1, k2, 16, 16, bufsize, mac);
	
	ECRYPT_keysetup(&x, k3, 128, 64); // key를 이용한 설정
	ECRYPT_ivsetup(&x, n);
	ECRYPT_encrypt_bytes(&x, mac, mac, 16);


	printf("mac = ");
	for (i = 0; i<16; i++)
		printf("%x ", mac[i]);
	printf("\n\n");


	ECRYPT_keysetup(&x, key, 128, 64);
	ECRYPT_ivsetup(&x, n);

	ECRYPT_decrypt_bytes(&x, buf, buf, bufsize); //키,input,output,input크기
												 /*
												 printf("stream cipher 16bytes: ");
												 for (i = 0; i<16; i++)
												 printf("%x ", buf[i]);
												 printf("\n\n\n");*/

	for (i = 0; i < bufsize; i++) {
		printf("0x%x, ", buf[i]);
	}
	printf("\n\n");

	
	
}
